<?php/*Дикий метод рендер - немного не укладывается в головеяпонял - когда запускается рендер, экшн уже выполнен и контент - не просто звук, а уже кусок кода HTML, запущенный через темплейт*/abstract class C_Base extends C_Controller{	protected $title;			protected $content;			function __construct()	{			}		protected function before()	{		$this->title = 'Abstract Shop';		$this->content = '';	}			public function render()	{		$vars = array('title' => $this->title, 'content' => $this->content);			$page = $this->template('v/v_main.php', $vars);						echo $page;	}	}