<?php/*Базовый класс контроллерапрокомментирую позжеОставляю методы гет и пост - не знаю пока зачем, но они прикольныеПереименовываю все нафиг - под стандарт кода*/abstract class C_Controller{	protected abstract function render();		protected abstract function before();		public function request($action){		$this->before();		$this->$action(); 		$this->render();	}		protected function isGet()	{		return $_SERVER['REQUEST_METHOD'] == 'GET';	}	protected function isPost()	{		return $_SERVER['REQUEST_METHOD'] == 'POST';	}	protected function template($fileName, $vars = array())	{		foreach ($vars as $key => $value) {			$$key = $value;		}		ob_start();		include "$fileName";		return ob_get_clean();		}			public function __call($name, $params){        die('Не понял, что за киллер... Попробую вызвать');	}}