<?phpinclude_once('m/model.php');class C_Page extends C_Base{		public function action_index(){		$this->title = 'Каталог товаров';		$text = text_get();		$this->content = $this->Template('v/v_index.php', array('text' => $text));		}		public function action_cabinet(){		$this->title = 'Личный кабинет';				$this->content = $this->Template('v/v_cabinet.php', array('text' => $text));			}	public function action_login(){		$this->title = 'Вход в личный кабинет';		if($this->isPost()){			check_pass($_POST['login'], $_POST['password']) ? 				$this->action_cabinet() : 				$this->action_index();		} else {			$this->content = $this->Template('v/v_login.php', array('text' => $text));		}	}}